import React from "react";
import "./styles/index.css";
class MainMenu extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      burger_active: false,
    };

    this.mobile_nav = this.mobile_nav.bind(this);
  }

  mobile_nav()
  {
      this.setState({burger_active: !this.state.burger_active});

      const navLinks = document.querySelectorAll('.mobile_ul li');
      navLinks.forEach((link, index) => {  
        console.log(index);

        if(link.style.animation){
            link.style.animation = '';
        }
        else{
             link.style.animation = `navLinkFade 0.5s ease forwards ${index / 7 + 0.3}s`;
             
             console.log( index/7);
            // link.style.opacity =('1');
        }
    }
      );
  }
  render() {
    return (
      <nav className="main_navi">
        <ul className="main_ul">
          <li>
            <a className="navi_item" href="#">
              Home
            </a>
          </li>
          <li>
            <a className="navi_item" href="#">
              Our Apartment
            </a>
          </li>
          <li>
            <a className="navi_item" href="#">
              Maledives
            </a>
          </li>
          <li>
            <a className="navi_item" href="#">
              Contact
            </a>
          </li>
          <li>
            <a className="navi_item" href="#">
              Book
            </a>
          </li>
        </ul>

        <div
          className={
            this.state.burger_active ? "burger burger_active" : "burger"
          }
        //   onClick={() =>
        //     this.setState({ burger_active: !this.state.burger_active })
        onClick={() => this.mobile_nav() 
          }
        >
          <div className="bar1"></div>
          <div className="bar2"></div>
          <div className="bar3"></div>
        </div>

       <div className={this.state.burger_active ? "mobile_nav active" : "mobile_nav"}>
          <ul className="mobile_ul">
            <li>
              <a className="navi_item" href="#">
                Home
              </a>
            </li>
            <li>
              <a className="navi_item" href="#">
                Our Apartment
              </a>
            </li>
            <li>
              <a className="navi_item" href="#">
                Maledives
              </a>
            </li>
            <li>
              <a className="navi_item" href="#">
                Contact
              </a>
            </li>
            <li>
              <a className="navi_item" href="#">
                Book
              </a>
            </li>
          </ul>
        </div>
        
      </nav>
    );
  }
}

export default MainMenu;
