import React from "react";
//  import BookingCalendar from 'react-event-booking-calendar'
import { TiArrowSortedUp } from "react-icons/ti";
import { TiArrowSortedDown } from "react-icons/ti";
import { GoCalendar } from "react-icons/go";
class Search extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      adults_counter: 1,
      kids_counter: 0,
      open_calendar: false,
      leave_callendar: false,
      bookings: [
        new Date(2020, 8, 1),
        new Date(2020, 8, 2),
        new Date(2020, 8, 3),
        new Date(2020, 8, 9),
        new Date(2020, 8, 10),
        new Date(2020, 9, 2),
        new Date(2020, 9, 3),
      ],
    };

    this.adultsCounter = this.adultsCounter.bind(this);
    this.kidsCounter = this.kidsCounter.bind(this);
    this.handleDayClick = this.handleDayClick.bind(this);
    this.openCallendar = this.openCallendar.bind(this);
    this.leaveCallendar = this.leaveCallendar.bind(this);
  }

  openCallendar() {
    this.setState({
      open_calendar:
        this.open_calendar === true
          ? (this.open_calendar = false)
          : (this.open_calendar = true),
    });
  }
  leaveCallendar() {
    this.setState({
      leave_callendar:
        this.leave_callendar === true
          ? (this.leave_callendar = false)
          : (this.leave_callendar = true),
    });
  }
  handleDayClick(event) {
    console.log(event);
  }

  adultsCounter(event) {
    this.setState((prevState) => {
      return {
        adults_counter:
          event === "add"
            ? prevState.adults_counter + 1
            : prevState.adults_counter - 1,
      };
    });
  }
  kidsCounter(event) {
    this.setState((prevState) => {
      return {
        kids_counter:
          event === "add"
            ? prevState.kids_counter + 1
            : prevState.kids_counter - 1,
      };
    });
  }

  render() {
    return (
      <form>
        <div className="search_list">
          <div className="search_element">
            <label className="date_picker_label">Check In</label>
            <button
              type="button"
              className="btn_search date_picker"
              onClick={() => this.openCallendar()}
            >
              DD MM YYYY
              <span className="calendar">
                {" "}
                <GoCalendar />
              </span>
            </button>
            {/* {this.state.open_calendar ? <BookingCalendar  bookings={this.state.bookings} clickable="true"/>  : null} */}
          </div>
          <div className="search_element">
            <label className="date_picker_label">Check Out</label>
            <button
              type="button"
              onClick={() => this.leaveCallendar()}
              className="btn_search date_picker"
            >
              DD MM YYYY
              <span className="calendar">
                {" "}
                <GoCalendar />
              </span>
            </button>
            {/* {this.state.leave_callendar ? <BookingCalendar  bookings={this.state.bookings} clickable="true"/>  : null} */}
          </div>
          <div className="small_form">
            <div className="search_element">
              <label className="preference_label">Adults</label>
              <div className="btn_search btn_counter">
                <span className="adults_counter">
                  {this.state.adults_counter}
                </span>
                <span className="arrows">
                  <button
                    type="button"
                    className="arrow up"
                    onClick={() => this.adultsCounter("add")}
                  >
                    {" "}
                    <TiArrowSortedUp />
                  </button>
                  <button
                    type="button"
                    disabled={this.state.adults_counter <= 0}
                    className="arrow down"
                    onClick={() => this.adultsCounter("sub")}
                  >
                    {" "}
                    <TiArrowSortedDown />
                  </button>
                </span>
              </div>
            </div>
            <div className="search_element">
              <label className="preference_label">Kids</label>
              <div className="btn_search btn_counter">
                <span className="adults_counter">
                  {this.state.kids_counter}
                </span>
                <span className="arrows">
                  <button
                    type="button"
                    className="arrow up"
                    onClick={() => this.kidsCounter("add")}
                  >
                    {" "}
                    <TiArrowSortedUp />
                  </button>
                  <button
                    type="button"
                    disabled={this.state.kids_counter <= 0}
                    className="arrow down"
                    onClick={() => this.kidsCounter("sub")}
                  >
                    {" "}
                    <TiArrowSortedDown />
                  </button>
                </span>
              </div>
            </div>
          </div>
          <div className="search_element">
            <button className="btn_search search_confirm">Search</button>
          </div>
        </div>
      </form>
    );
  }
}
export default Search;
